package main

import (
	"bufio"
	"fmt"
	"os"
	"strconv"
	"strings"
)

func main() {
	reader := bufio.NewReader(os.Stdin)

	// Считываем количество элементов
	//fmt.Print("Введите количество элементов в массиве: ")
	nStr, _ := reader.ReadString('\n')
	nStr = strings.TrimSpace(nStr)
	n, err := strconv.Atoi(nStr)
	if err != nil {
		fmt.Println("Ошибка: введите целое число")
		return
	}

	// Считываем элементы массива
	//fmt.Printf("Введите %d чисел через пробел: ", n)
	arrayStr, _ := reader.ReadString('\n')
	arrayStr = strings.TrimSpace(arrayStr)
	arrayStrSlice := strings.Split(arrayStr, " ")

	// Проверяем, что количество введенных чисел совпадает с N
	if len(arrayStrSlice) != n {
		fmt.Println("Ошибка: количество введенных чисел не совпадает с N")
		return
	}

	// Преобразуем строковые значения в целые числа
	array := make([]int, n)
	for i, s := range arrayStrSlice {
		array[i], err = strconv.Atoi(s)
		if err != nil {
			fmt.Printf("Ошибка: '%s' не является целым числом\n", s)
			return
		}
	}

	// Выводим элементы с четными индексами
	//fmt.Println("Элементы с четными индексами:")
	count := 0
	for i := 0; i < n; i += 1 {
		if array[i] > 0 {
			count++
		}
	}
	str := ""
	fmt.Println(count)
}
